import React from 'react';
import 'antd/dist/antd.css';
import {
  Form,
  Input,
  Button,
  Divider,
  Icon
} from 'antd';
import { IncomingMessage } from 'http';

const { Search } = Input;
class App extends React.Component {

  componentWillMount() {

    document.title = "登录 -  涂叶云";
  }



  render() {

    const styles = <style jsx>
      {
        `
        body,html{
          overflow-y:hidden;
          min-width:960px;
          background:#f9f9f9;
          padding:0;
          margin:0;
        }
        @font-face {
          font-family: 'webfont';
          font-display: swap;
          src: url('//at.alicdn.com/t/webfont_ldauui3dlc.eot'); /* IE9*/
          src: url('//at.alicdn.com/t/webfont_ldauui3dlc.eot?#iefix') format('embedded-opentype'), /* IE6-IE8 */
          url('//at.alicdn.com/t/webfont_ldauui3dlc.woff2') format('woff2'),
          url('//at.alicdn.com/t/webfont_ldauui3dlc.woff') format('woff'), /* chrome、firefox */
          url('//at.alicdn.com/t/webfont_ldauui3dlc.ttf') format('truetype'), /* chrome、firefox、opera、Safari, Android, iOS 4.2+*/
          url('//at.alicdn.com/t/webfont_ldauui3dlc.svg#Alibaba-PuHuiTi-Regular') format('svg'); /* iOS 4.1- */
        }
        `
      }
    </style>
    return (
      <div>
        <table style={{ width: "100%" }}>
          <tr>
            <td style={{ width: "30%" }}>
              <img src="https://twoyecloud.oss-cn-beijing.aliyuncs.com/gIXJiJtjEhFgmrEVjkNL.png" style={{ height: "100vh" }} />
            </td>
            <td>
              <div style={{ width: "450px", margin: "0 auto" }}>
                <h1 style={{ fontFamily: "webfont", textAlign: "center" }}>欢迎来到云凤蝶</h1>
                <p style={{ textAlign: "center", marginBottom: "40px" }}>极简制作 一键呈现</p>
                <Form>
                  <Form.Item
                    label="手机号码"
                  >
                    <Input size="large" />
                  </Form.Item>
                  <Form.Item
                    label="验证码"
                  >
                    <Search
                      enterButton="发送验证码"
                      size="large"
                      onSearch={value => console.log(value)}
                    />
                  </Form.Item>
                  <Form.Item>
                    <Button
                      shape="round"
                      size="large"
                      style={{ width: "150px", display: "block", margin: "0 auto", marginTop: "30px" }}
                      type="primary"
                      loading
                    >登录</Button>
                  </Form.Item>
                  <Form.Item>

                  </Form.Item>
                </Form>
                <p style={{ textAlign: "center", color: "#999" }}>———————— 推荐使用快捷方式 ————————</p>
                <p style={{ textAlign: "center", color: "#999" }}>
                  <Icon type="alipay-circle" theme="filled" />
                </p>
              </div>
            </td>
          </tr>
        </table>
        {styles}
      </div>


    );
  }
}

export default App;
